body {
    overflow: visible;
}

.preload {
    background: #f1f2f3;
    background-size: cover;
    z-index: 90;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;

    span {
        width: 100%;
        height: 100px;
        border-radius: 40px;
        color: #000;
        display: flex;
        justify-content: center;
        align-items: center;
        transform-origin: top;
        z-index: 10;
        position: relative;
        font-size: 25px;
        padding: 20px 0;
        font-weight: 500;

        
    }

    .contagem-preload {
        
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        height: 360px;
        position: relative;
        animation: cameraPan 6s infinite;
        transition: all .3s cubic-bezier(.645,.045,.355,1);

        svg {
            text-shadow: 0 0 50px #d90111;
            animation: animate 3s linear;

            @keyframes animate {
                0% { filter: opacity(0.4);}

                100% { filter: opacity(1);}
            }
        }
    }

    svg {
        width: 100%;
    }
}

@keyframes cameraPan {
    0% {
        transform: translateX(0);
    }
    25% {
        transform: translateX(0);
    }
    50% {
        transform: translateY(0);
    }
    75% {
        transform: translateY(0);
    }
    100% {
        transform: translateX(0);
    }
}

@media (max-width:768px){
    .preload {
        justify-content: center;
        padding-top: 60px;
    }

    .contagem-preload {
        justify-content: center;
    }

    .full-color, .low-color {
        width: 100px;
    }
}
